import React from 'react';
import LoaderSpinner from 'react-loader-spinner';
import 'react-loader-spinner/dist/loader/css/react-spinner-loader.css';
import PropTypes from 'prop-types';

const Loader = ({ isLoading }) => {
  return (
    <div style={{ display: isLoading ? 'block' : 'none' }}>
      <LoaderSpinner
        type="Oval" // Змініть тип спінера за потреби (https://www.npmjs.com/package/react-loader-spinner#props)
        color="#00BFFF" // Змініть колір за потреби
        height={50} // Змініть висоту за потреби
        width={50} // Змініть ширину за потреби
      />
    </div>
  );
};

Loader.propTypes = {
  isLoading: PropTypes.bool.isRequired,
};

export default Loader;
