import React from 'react';

const Modal = ({ isOpen, onClose, imageUrl }) => {
  return (
    isOpen && (
      <div className="overlay" onClick={onClose}>
        <div className="modal">
          <img src={imageUrl} alt="" />
        </div>
      </div>
    )
  );
};

export default Modal;
